import React, { useState, useEffect } from "react";
import axios from "axios";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    country: "",
    state: "",
    city: "",
    skillLevel: "",
  });

  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);
  const [cities, setCities] = useState([]);

  // Fetch countries on component mount
  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await axios.get("https://restcountries.com/v3.1/all");
        const countryList = response.data.map((country) => ({
          name: country.name.common,
          alpha2Code: country.cca2,
        }));
        setCountries(countryList);
      } catch (error) {
        console.error("Error fetching countries:", error);
      }
    };

    fetchCountries();
  }, []);

  // Fetch states based on selected country
  const handleCountryChange = async (e) => {
    const selectedCountry = e.target.value;
    setFormData({
      ...formData,
      country: selectedCountry,
      state: "",
      city: "",
    });

    // Clear states and cities when country changes
    setStates([]);
    setCities([]);

    if (!selectedCountry) return;

    // Fetch states using the selected country
    try {
      // Replace this URL with your actual API endpoint for fetching states
      const response = await axios.get(
        `https://api.example.com/states?country=${selectedCountry}`
      );
      console.log("States Response:", response.data); // Debugging log

      if (response.data && Array.isArray(response.data)) {
        setStates(response.data);
      } else {
        console.error("Invalid state data format:", response.data);
        // For testing, use hardcoded states
        setStates([
          { code: "CA", name: "California" },
          { code: "TX", name: "Texas" },
          { code: "NY", name: "New York" },
        ]);
      }
    } catch (error) {
      console.error("Error fetching states:", error);
      // Use hardcoded states for testing if the API call fails
      setStates([
        { code: "CA", name: "California" },
        { code: "TX", name: "Texas" },
        { code: "NY", name: "New York" },
      ]);
    }
  };

  // Fetch cities based on selected state
  const handleStateChange = async (e) => {
    const selectedState = e.target.value;
    setFormData({
      ...formData,
      state: selectedState,
      city: "",
    });

    // Clear cities when state changes
    setCities([]);

    if (!selectedState) return;

    // Fetch cities using the selected state
    try {
      const response = await axios.get(
        `https://api.example.com/cities?state=${selectedState}`
      );
      console.log("Cities Response:", response.data); // Debugging log

      if (response.data && Array.isArray(response.data)) {
        setCities(response.data);
      } else {
        console.error("Invalid city data format:", response.data);
      }
    } catch (error) {
      console.error("Error fetching cities:", error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5000/register",
        formData
      );
      console.log("Registration successful:", response.data);
    } catch (error) {
      console.error("Error during registration:", error);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Register for Pickleball</h2>
      <form onSubmit={handleSubmit} className="mt-4">
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            className="form-control"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            className="form-control"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            className="form-control"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Country:</label>
          <select
            className="form-control"
            name="country"
            value={formData.country}
            onChange={handleCountryChange}
          >
            <option value="">Select Country</option>
            {countries.map((country) => (
              <option key={country.alpha2Code} value={country.alpha2Code}>
                {country.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>State:</label>
          <select
            className="form-control"
            name="state"
            value={formData.state}
            onChange={handleStateChange}
            disabled={!formData.country}
          >
            <option value="">Select State</option>
            {states.map((state) => (
              <option key={state.code} value={state.code}>
                {state.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>City:</label>
          <select
            className="form-control"
            name="city"
            value={formData.city}
            onChange={handleChange}
            disabled={!formData.state}
          >
            <option value="">Select City</option>
            {cities.map((city) => (
              <option key={city.id} value={city.name}>
                {city.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Skill Level:</label>
          <select
            className="form-control"
            name="skillLevel"
            value={formData.skillLevel}
            onChange={handleChange}
          >
            <option value="">Select Skill Level</option>
            <option value="Beginner">Beginner</option>
            <option value="Intermediate">Intermediate</option>
            <option value="Advanced">Advanced</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary btn-block mt-3">
          Register
        </button>
      </form>
    </div>
  );
};

export default Register;
