import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Register.css"; // Import your CSS file for custom styles
import Footer from "./Footer"; // Import the Footer component

const Register = () => {
  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);
  const [cities, setCities] = useState([]);

  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedState, setSelectedState] = useState("");
  const [selectedCity, setSelectedCity] = useState("");

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  // Fetch Countries
  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await axios.get(
          "https://api.countrystatecity.in/v1/countries",
          {
            headers: { "X-CSCAPI-KEY": process.env.REACT_APP_COUNTRY_API_KEY }, // Use your API key from .env
          }
        );
        const countryList = response.data.map((country) => ({
          name: country.name,
          code: country.iso2,
        }));
        setCountries(countryList.sort((a, b) => a.name.localeCompare(b.name)));
      } catch (error) {
        console.error("Error fetching countries:", error);
      }
    };
    fetchCountries();
  }, []);

  // Fetch States
  useEffect(() => {
    if (selectedCountry) {
      const fetchStates = async () => {
        try {
          const response = await axios.get(
            `https://api.countrystatecity.in/v1/countries/${selectedCountry}/states`,
            {
              headers: {
                "X-CSCAPI-KEY": process.env.REACT_APP_COUNTRY_API_KEY,
              },
            }
          );
          setStates(response.data);
          setSelectedState("");
          setCities([]); // Reset cities when country changes
        } catch (error) {
          console.error("Error fetching states:", error);
        }
      };
      fetchStates();
    }
  }, [selectedCountry]);

  // Fetch Cities
  useEffect(() => {
    if (selectedState) {
      const fetchCities = async () => {
        try {
          const response = await axios.get(
            `https://api.countrystatecity.in/v1/countries/${selectedCountry}/states/${selectedState}/cities`,
            {
              headers: {
                "X-CSCAPI-KEY": process.env.REACT_APP_COUNTRY_API_KEY,
              },
            }
          );
          setCities(response.data);
          setSelectedCity("");
        } catch (error) {
          console.error("Error fetching cities:", error);
        }
      };
      fetchCities();
    }
  }, [selectedState]);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Data:", formData);
    console.log("Country:", selectedCountry);
    console.log("State:", selectedState);
    console.log("City:", selectedCity);
    // You can add navigation to the main page here if needed
  };

  return (
    <div
      className="container mt-5"
      style={{
        backgroundColor: "#1c1c1c",
        color: "#fff",
        padding: "20px",
        borderRadius: "8px",
      }}
    >
      <h2 className="text-center">Register for Pickleball</h2>
      <form onSubmit={handleSubmit} className="mt-4">
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            className="form-control"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            className="form-control"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            className="form-control"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>

        {/* Country Dropdown */}
        <div className="form-group">
          <label>Country:</label>
          <select
            className="form-control"
            value={selectedCountry}
            onChange={(e) => setSelectedCountry(e.target.value)}
            required
          >
            <option value="">Select Country</option>
            {countries.map((country) => (
              <option key={country.code} value={country.code}>
                {country.name}
              </option>
            ))}
          </select>
        </div>

        {/* State Dropdown */}
        {states.length > 0 && (
          <div className="form-group">
            <label>State:</label>
            <select
              className="form-control"
              value={selectedState}
              onChange={(e) => setSelectedState(e.target.value)}
              required
            >
              <option value="">Select State</option>
              {states.map((state) => (
                <option key={state.iso2} value={state.iso2}>
                  {state.name}
                </option>
              ))}
            </select>
          </div>
        )}

        {/* City Dropdown */}
        {cities.length > 0 && (
          <div className="form-group">
            <label>City:</label>
            <select
              className="form-control"
              value={selectedCity}
              onChange={(e) => setSelectedCity(e.target.value)}
              required
            >
              <option value="">Select City</option>
              {cities.map((city) => (
                <option key={city.iso2} value={city.iso2}>
                  {city.name}
                </option>
              ))}
            </select>
          </div>
        )}

        <button type="submit" className="btn btn-primary mt-3">
          Register
        </button>
      </form>
      <Footer /> {/* Include the Footer here */}
    </div>
  );
};

export default Register;
